//! BIP-174 test vectors - fails signer checks.
//!
//! This is everything in the test vectors section of the bip between the lines:
//!
//! > Fails Signer checks
//!
//! > The private keys in the tests below are derived from the following master private key:

use super::hex_psbt;
use crate::psbt::serialize::Psbt;

#[track_caller]
fn assert_fails_signer_checks_v0(hex: &str, base64: &str) {
    let psbt = hex_psbt(hex).expect("valid hex");
    // If we got this far decoding works so this is basically just a sanity check.
    let base64 = base64.parse::<Psbt>().unwrap();
    assert_eq!(base64, psbt);

    let psbt = crate::Psbt::from_serializable(psbt).expect("valid v0 PSBT");
    assert!(psbt.signer_checks().is_err())
}

#[test]
fn bip174_fails_signer_checks() {
    // Case: A Witness UTXO is provided for a non-witness input
    let hex = "";
    let base64 = "";
    assert_fails_signer_checks_v0(hex, base64);

    // Case: redeemScript with non-witness UTXO does not match the scriptPubKey
    let hex = "";
    let base64 = "";
    assert_fails_signer_checks_v0(hex, base64);

    // Case: redeemScript with witness UTXO does not match the scriptPubKey
    let hex = "";
    let base64 = "";
    assert_fails_signer_checks_v0(hex, base64);

    // Case: witnessScript with witness UTXO does not match the redeemScript
    let hex = "";
    let base64 = "";
    assert_fails_signer_checks_v0(hex, base64);
}
