    // Case: PSBTv2 missing PSBT_GLOBAL_INPUT_COUNT.

    let hex = "";
    let base64 = "";
    assert!(is_invalid_v0(hex, base64));
    assert!(is_invalid_v2(hex, base64));

    // Case: PSBTv2 missing PSBT_GLOBAL_OUTPUT_COUNT.
    let hex = "";
